import numpy as npdef softmax(x):    y = []    b = np.sum(np.exp(x))    for i in x:        a = np.exp(i)        y.append(a/b)    return yx = [1,2,3]y = softmax(x)print(y)def softmax2(x):    x_sum = np.sum(np.exp(x))    y = np.exp(x) / x_sum    return yy2 = softmax2(x)print(y2)y1 = np.exp(1) / (np.exp(1)+np.exp(2)+np.exp(3))y2 = np.exp(2) / (np.exp(1)+np.exp(2)+np.exp(3))y3 = np.exp(3) / (np.exp(1)+np.exp(2)+np.exp(3))print(y1 ,y2 ,y3)